Ability.Lua:
if PQR_SpellAvailable(49206) and PQ_ValidUnit and IsSpellInRange(GetSpellInfo(45524), PQR_CustomTarget) == 1 then local targetHealth = 100 * UnitHealth(PQR_CustomTarget) / UnitHealthMax(PQR_CustomTarget)
if targetHealth &lt;= Nova_EmpowerRuneWeapon and Nova_EmpowerRuneWeapon_Check then local runeCheck = { RuneCheck() } local total = 0 for i=1, #runeCheck do total = runeCheck[i] + total end
if total &lt; 2 then return true end end end

local SD = { UnitBuff(&quot;player&quot;, GetSpellInfo(81340)) } local DT = { UnitBuff(&quot;pet&quot;, GetSpellInfo(91342)) }
if PQR_SpellAvailable(47541) and PQ_ValidUnit and IsSpellInRange(GetSpellInfo(47541), PQR_CustomTarget) == 1 then if SD[1] then if DT[1] then local DTleft = DT[7] - GetTime() if DTleft &lt; 5 and SD[7] - GetTime() - DTleft &gt;= 2 then return false end end
return true end end
